function calculate(inputs) {
  const { homeValue, mortgageBalance, loanAmount, interestRate, loanTerm } = inputs;
  const availableEquity = homeValue - mortgageBalance;
  const monthlyRate = interestRate / 100 / 12;
  const numPayments = loanTerm * 12;

  if (loanAmount > availableEquity * 0.85) {
    throw new Error('Loan amount exceeds 85% of available equity');
  }

  const monthlyPayment = loanAmount * (monthlyRate * Math.pow(1 + monthlyRate, numPayments)) / 
                        (Math.pow(1 + monthlyRate, numPayments) - 1);
  const totalPayment = monthlyPayment * numPayments;
  const totalInterest = totalPayment - loanAmount;

  return {
    monthlyPayment: Math.round(monthlyPayment * 100) / 100,
    totalPayment: Math.round(totalPayment * 100) / 100,
    totalInterest: Math.round(totalInterest * 100) / 100,
    availableEquity: Math.round(availableEquity * 100) / 100
  };
}

function calculate(inputs) {
  const values = Object.values(inputs).filter(v => typeof v === 'number' && !isNaN(v));
  if (values.length === 0) return { result: 0 };

  const sum = values.reduce((a, b) => a + b, 0);
  const average = sum / values.length;
  const max = Math.max(...values);
  const min = Math.min(...values);

  return {
    result: Math.round(sum * 100) / 100,
    sum: Math.round(sum * 100) / 100,
    average: Math.round(average * 100) / 100,
    max,
    min,
    count: values.length
  };
}

