function calculate(inputs) {
  const { shape, length, width, radius } = inputs;

  switch (shape || 'rectangle') {
    case 'rectangle':
      return { area: length * width };
    case 'circle':
      return { area: Math.PI * radius * radius };
    case 'triangle':
      return { area: 0.5 * length * width };
    case 'square':
      return { area: length * length };
    default:
      return { area: length * width };
  }
}

